/**
 * Created by Stef van den Berg
 * on 30/01/14
 *
 * This plugin is created to animate the label to placeholder. When the focus is in the input the label will animate to its original place.
 * When the field is still empty the label will go back as placeholder.
 *
 * Settings:
 *
 *  distanceToTop :     default 29px
 *  distanceToLeft:     default 4px
 *  colorLabel:         default #000000
 *  colorPlaceholder    default #999999
 */
(function(e){e.fn.labelAsPlaceholder=function(t){var n=e.extend({distanceLabelToInput:"29px",distanceToLeft:"4px",defaultLabel:"#000000",colorPlaceholder:"#999999",colorLabelOnError:"#990000"},t);return this.each(function(){var t=e(this);var r=n.defaultLabel;var i=t.parent();var s=i.find("label");if(i.hasClass("error")){r=n.colorLabelOnError}else{r=n.colorPlaceholder}if(t.val()==""){if(i.hasClass("error")){r=n.colorLabelOnError}if(t.attr("type")=="date"){s.css("visibility","hidden")}else{s.css("position","relative");s.animate({top:n.distanceLabelToInput,left:n.distanceToLeft,color:r})}}if(t.attr("type")=="date"){t.bind("focus",function(){var e=n.defaultLabel;if(i.hasClass("error")){e=n.colorLabelOnError}s.animate({color:e});s.css("visibility","visible")});t.bind("blur",function(){var t=e(this);if(t.val()==""){var r=n.colorPlaceholder;if(i.hasClass("error")){r=n.colorLabelOnError}s.animate({color:r});s.css("visibility","hidden")}})}else{t.bind("focus",function(){var e=n.defaultLabel;if(i.hasClass("error")){e=n.colorLabelOnError}s.animate({top:0,color:e})});t.bind("blur",function(){var t=e(this);if(t.val()==""){var r=n.colorPlaceholder;if(i.hasClass("error")){r=n.colorLabelOnError}s.animate({top:n.distanceLabelToInput,color:r})}})}})}})(jQuery)
